import React from "react";
import { useSelector } from "react-redux";

const userinfo=()=>{
    const user = useSelector((state)=>state.user.userName)
    return (
        <div className="flexContainer">
            <div>
                Hello, {user}
            </div>

            <div>
                Logout
            </div>
        </div>
    )
}

export default userinfo;
