import React from "react";
import styled from "styled-components";
import QuantityComp from "./quantityComp";
import { useSelector } from "react-redux";
const Wrapper = styled.div``;

const Div = styled.div``

const Img = styled.img``

const Button = styled.button``

const H2 = styled.h2``

const P = styled.p``

const H3 = styled.h3``

const cartComp = (props) => {

    const cart = useSelector((state)=>state.cart)

    return (
        <Wrapper className="cartwrapper">
            <Div className="Cartmain1">
                    <Div className="imgcont">
                        <Img className="CartmainProduct" src={props.imgURL} />
                    </Div>
            </Div>

                <Div className="Cartmain2">
                    <H2 className="productName">
                        {props.name}
                    </H2>
                    <P className="productDisc">{props.discription}</P>
                    <H3 className="productPrice">{props.price}</H3>
                    <QuantityComp />
                    <Button className="btn btn-remove">Remove</Button>
                </Div>
        </Wrapper>
    );
}

export default cartComp;
