import React from "react";
import { Link } from "react-router-dom";
import menuBar from "../icons/bars-solid.svg";
import cartComp from "../icons/cart-shopping-solid (2).svg"
import userIcon from "../icons/user-solid.svg"
import { useSelector } from "react-redux";

const Navbar = () => {

    const cart = useSelector((state)=>state.cart);
    const user = useSelector((state)=>state.user.currentUser.username)

    const checkConditionally=()=>{
        if(user!==""){
            return(
                <div>
                    Hello,{user}
                </div>
            )
        }
        else{
            return(
                <button className="nav_btn"><a href="/login" className="a">Login</a></button>
            )
        }
    }

    return (
    <div className="ok">
        <div className="first">
            <img typeof="button" className="menuBar" src={menuBar} />
        </div>
        <div className="second">
            <div ><a href="/" className="nice">Home</a></div>
            <div ><a href="/products" className="nice">Products</a></div>
            <div className="nice">About</div>
            <div className="nice">Contact</div>
        </div>
        
        <div className="third">
                <div style={{
                    display:"flex",
                    alignItems:"center"
                    }}>
                    <div style={{
                        position:"relative",
                        
                        }}>
                        <Link to="/cart">
                        <img
                        style={{
                            width:"40px"
                        }}
                        typeof="button" 
                        src={cartComp}/>
                        </Link>
                        <div 
                                style={{
                                    borderRadius:"15px",
                                    paddingRight:"5px",
                                    paddingLeft:"5px",
                                    color:"white",
                                    fontSize:"15px",
                                    backgroundColor:"#6D67E4",
                                    textAlign:"center",
                                    border:"solid",
                                    position:"absolute",
                                    top:"-11px",
                                    right:"-8px"
                                }}>
                                    {cart.quantity}
                        </div>
                    </div>
                </div>
                <div 
                style=
                {
                    {
                        width:"50%"
                    }
                }>
                    {checkConditionally()}
            </div>
        </div>
    </div>
    );
}

export default Navbar;

