import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import SingleProduct from "../components/SingleProduct";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import { addProduct } from "../redux/userCart";
import Single from "../newSingle"
import ProductList from "../components/ProductList";
import { useLocation } from "react-router-dom";
import axios from "axios";


const Container = styled.div``

const Wrapper = styled.div``

const Div = styled.div``

const Img = styled.img``

const Button = styled.button``

const H2 = styled.h2``

const P = styled.p``

const H3 = styled.h3``

const createSingleproduct = (single) => {
    console.log(single)
    const dispatch = useDispatch();
    const quantity=0;
    const handleClick=()=>{
        dispatch(addProduct({single,quantity}))
    }
    return (
        <div>
            <Wrapper         className="swrapper">
                <Div className="main1">
                    <Div className="smallImg">
                        <Img className="sideImg" src={single.img1URL} />
                        <Img className="sideImg" src={single.img2URL} />
                        <Img className="sideImg" src={single.img3URL} />
                        <Img className="sideImg" src={single.img4URL} />
                        <Img className="sideImg" src={single.img5URL} />
                    </Div>
                    <Div className="mainImg">
                        <Div>
                            <Img className="mainProduct" src={single.imgURL} />
                        </Div>
                        <Div className="btn-single">
                            <Button className="btn btns">Buy Now</Button>
                            <Button 
                            onClick={handleClick}
                            className="cart-btn btn btns">Add to Cart</Button>
                        </Div>
                    </Div>
                </Div>
                <Div className="main2">
                    <H2 className="productName">
                        {single.name}
                    </H2>
                    <P className="productDisc">{single.discription}</P>
                    <H3 className="productPrice">{single.email}</H3>
                </Div>
            </Wrapper>
    </div>
    );
}

const singleProductComp =()=>{
    const loc = useLocation();
let proId=loc.pathname.split("/")[2];
const [proObject,setObject] = useState({});

useEffect(()=>{
    axios
        .get("http://localhost:8000/product/"+proId)
        .then((res)=>
            setObject(res.data)
        );
})

return(
    <div className="scontainer">
        {createSingleproduct(proObject)}
    </div>
)
}
export default singleProductComp;
