import { createSlice } from "@reduxjs/toolkit";

export const userCart=createSlice({
    name:"cart",
    initialState:{
        poducts:[],
        quantity:0
    },
    reducers:{
        addProduct:(state,action)=>{
            state.quantity += 1;
            state.poducts.push(action.payload)
        }
    }
});

export const {addProduct} = userCart.actions;
export default userCart.reducer;
