import React from "react";
import styled from "styled-components";
import QuantityComp from "./quantityComp";
import { useSelector } from "react-redux";
const Wrapper = styled.div``;

const Div = styled.div``

const Img = styled.img``

const Button = styled.button``

const H2 = styled.h2``

const P = styled.p``

const H3 = styled.h3``

const cartComp = () => {

    const cart = useSelector((state)=>state.cart)

    return (
        //its is rendering that array agian and again
        //solve it today
        <Div>
            {pcart.roducts.map((product)=>(
            <Wrapper className="cartwrapper">
                
                    <Div className="Cartmain1">
                            <Div className="imgcont">
                                <Img className="CartmainProduct" src={product.imgURL} />
                            </Div>
                    </Div>

                        <Div className="Cartmain2">
                            <H2 className="productName">
                                {product.name}
                            </H2>
                            <P className="productDisc">{product.discription}</P>
                            <H3 className="productPrice">{product.price}</H3>
                            <QuantityComp />
                            <Button className="btn btn-remove">Remove</Button>
                        </Div>
                    
            </Wrapper>
        ))}
        </Div>
    );
}

export default cartComp;
