import React from "react";
import { Link } from "react-router-dom";
import menuBar from "../icons/bars-solid.svg";
import cartComp from "../icons/cart-shopping-solid (2).svg"
import userIcon from "../icons/user-solid.svg"
import { useSelector } from "react-redux";

const Navbar = () => {
    const quantity = useSelector((state)=>state.cart.quantity);
    const user = useSelector((state)=>state.user.userName)

    const checkConditionally=()=>{
        if(user!==""){
            return(
                <div style={{
                    border:"solid",
                    display:"flex",
                    alignItems:"center"
                }}>
                    <img style={{
                            width:"30px"
                        }} src={userIcon} />
                    <p style={{
                        fontSize:"1.4em",
                        marginLeft:"1px"
                    }}>Hello,<b>{user}</b></p>
                </div>
            )
        }
        else{
            return(
                <button className="nav_btn"><a style={{textDecoration:"none"}} href="/login" className="a">Login/Register</a></button>
            )
        }
    }

    const createDiv=()=>{
        if(quantity!==0){
            return(
                <div
                                style={{
                                    borderRadius:"15px",
                                    paddingRight:"5px",
                                    paddingLeft:"5px",
                                    color:"white",
                                    fontSize:"15px",
                                    backgroundColor:"#6D67E4",
                                    textAlign:"center",
                                    border:"solid",
                                    position:"absolute",
                                    top:"-11px",
                                    right:"-8px"
                                }}>
                                {
                                    quantity
                                }
                        </div>
            )
        }
    }

    return (
    <div className="ok">
        <div className="first">
            <img typeof="button" className="menuBar" src={menuBar} />
        </div>
        <div className="second">
            <div ><a href="/" className="nice">Home</a></div>
            <div ><a href="/products" className="nice">Products</a></div>
            <div className="nice">About</div>
            <div className="nice">Contact</div>
        </div>
        
        <div className="third">
                <div style={{
                    display:"flex",
                    alignItems:"center"
                    }}>
                    <div className="iconContainer">
                        <Link to="/cart">
                        <img
                        style={{
                            width:"40px"
                        }}
                        typeof="button" 
                        src={cartComp}/>
                        </Link>
                        {createDiv()}
                    </div>
                </div>
                <div 
                style=
                {
                    {
                        width:"50%"
                    }
                }>
                    {checkConditionally()}
            </div>
        </div>
    </div>
    );
}

export default Navbar;

