import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import SingleProduct from "../components/SingleProduct";
import Single from "../newSingle"
import ProductList from "../components/ProductList";
import { useLocation } from "react-router-dom";
import axios from "axios";


const createSingleproduct = (single) => {
    return (
        <SingleProduct 
            key = {single._id}
            name = {single.name}
            imgURL = {single.imgURL}
            email = {single.email}
            discription = {single.discription}
            img1 = {single.img1URL}
            img2 = {single.img2URL}
            img3 = {single.img3URL}
            img4 = {single.img4URL}
            img5 = {single.img5URL}
        />
    );
}   

const SingleProductpage = () => {
const loc = useLocation();
let proId=loc.pathname.split("/")[2];
const [proObject,setObject] = useState({});

useEffect(()=>{
    axios
        .get("http://localhost:8000/product/"+proId)
        .then((res)=>
            setObject(res.data)
        );
},{})

    return (<div>
    <Navbar />
    <div className="scontainer">
        {createSingleproduct(proObject)}
    </div>
    <ProductList />
    <Footer />
    </div>
    );
}
export default SingleProductpage;
